long mediaAnalog(){
    int media=10;
    long int soma=0;
    // Amostras
    for(int x=media; x>0; x--){
        soma+=analogRead(portAN); 
      	delay(1); 
    }
    // Média
    return=soma/media;

}// mediaAnalog

String debugPrint(String _strDado){
    Serial.print("[");
    Serial.print(_strDado);
    Serial.println("]");

}// end debugPrint

void piscaLed(byte _nPisca){
    for(byte x=nPisca; x>0; x--){
	digitalWrite(portLed, HIGH); delay(200);
	digitalWrite(portLed, LOW); delay(200);
    }

} // end piscaLed
